function get_input() {
    global $input;
    return $input;
}

function calculate_floor($input) {
    $floor = 0;
    for ($i = 0; $i < strlen($input); $i++) {
        $floor += $input[$i] == '(' ? 1 : -1;
        if ($floor == -1) {
            return $i + 1;
        }
    }
    return $floor;
}

function main() {
    $input = get_input();
    echo 'Character position: ' . calculate_floor($input) . PHP_EOL;
};

main();
